
public class _07Do_While {

	/***
	 * 循环do-while
	 * 		1.基本语法
	 * 		do{
	 * 			//执行语句
	 * 		}while()
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		do{
			System.out.println("喜欢....");
		}while(false);
		
		
		int i = 0;//初始化语句A
		do{
			System.out.println(i);//循环执行语C
			i++;//循环后语句C
		}while(i<=10);//循环判断语句B
		
	
	}

}
