
public class _05ArrayFor {

	/***
	 * 遍历数组
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[] num  = {1,2,3,4,5};
		//循环打印数组值；
		for(int i = 0;i<=num.length-1;i++){
			System.out.print(num[i]+" ");
		}
		for (int j = 0; j < num.length; j++) {
			System.out.print(num[j]+"  ");
		}
		
	}

}
